/**
 * @typedef RollupPluginOptions
 * @property {import('@rollup/pluginutils').FilterPattern} [include] List of picomatch patterns to include
 * @property {import('@rollup/pluginutils').FilterPattern} [exclude] List of picomatch patterns to exclude
 *
 * @typedef {import('../compile').CompileOptions & RollupPluginOptions} ProcessorAndRollupOptions
 */
/**
 * Compile MDX w/ rollup.
 *
 * @param {ProcessorAndRollupOptions} [options]
 * @return {import('rollup').Plugin}
 */
export function rollup(options?: ProcessorAndRollupOptions): import('rollup').Plugin;
export type RollupPluginOptions = {
    /**
     * List of picomatch patterns to include
     */
    include?: import('@rollup/pluginutils').FilterPattern;
    /**
     * List of picomatch patterns to exclude
     */
    exclude?: import('@rollup/pluginutils').FilterPattern;
};
export type ProcessorAndRollupOptions = import('../compile').CompileOptions & RollupPluginOptions;
