/**
 * A plugin to build JSX into function calls.
 * `estree-util-build-jsx` does all the work for us!
 *
 * @param {RecmaJsxBuildOptions} [options]
 * @returns {Transformer}
 */
export function recmaJsxBuild(options?: RecmaJsxBuildOptions): Transformer
export type Program = import('estree-jsx').Program
export type Transformer = import('unified').Transformer
export type RecmaJsxBuildOptions = {
  /**
   * Whether to keep the import of the automatic runtime or get it from `arguments[0]` instead
   */
  outputFormat?: 'program' | 'function-body'
}
