/**
 * Add the micromark and mdast extensions for MDX.js (JS aware MDX).
 *
 * @this {Processor}
 * @param {MdxOptions} [options]
 * @return {void}
 */
export function remarkMdx(options?: MdxOptions): void
export type Processor = import('unified').Processor
export type AcornParser = import('acorn').Parser
export type AcornOptions = import('acorn').Options
export type MdxOptions = {
  acorn?: AcornParser
  acornOptions?: AcornOptions
  addResult?: boolean
}
